mapper.select:

/** CouponMapper.findByAccountNo */
SELECT A.*
FROM TB_COUPON A
WHERE A.ACCOUNT_NO = #{accountNo}
AND ROWNUM <= 1


/** CouponMapper.findIssueTargetCoupon */
SELECT A.*
FROM TB_COUPON A
WHERE A.ACCOUNT_NO IS NULL
AND ROWNUM <= 1
/* FOR UPDATE */

mapper.update:
UPDATE TB_COUPON
SET ISS_DATE = NOW(),
    ACCOUNT_NO = #{accountNo}
WHERE COUPON_ID = #{couponId}
